import React, { useState } from 'react'
import { useParams } from 'react-router-dom'
import { getArticleById } from '../../Requests/articleId'
import * as s from './Article.styling'



export const Article = () => {
    let newsId = useParams();

    let idValue = Object.values(newsId).shift();
    console.log(idValue);
    const [post, setPost] = useState({});

    console.log(post);

    React.useEffect( () => {

        getArticleById(idValue)
        .then((response) => {
          console.log("response:", response);
          setPost(response.data)
      })
        .catch((error) => console.log("error:", error))

    }, []);

    return (
        <s.ArticleSection>
        <s.ArticleContainer>
            <img src={post.imageUrl} alt="Image Of the News"/>
            <h1>{post.title}</h1>
            <p>{post.summary}</p>
            <p>You can follow the link to read the full article on the source page: {post.url}</p>

        </s.ArticleContainer>
        </s.ArticleSection>
    )
};
