import styled from 'styled-components'


export const SliderWrap = styled.section`
   margin: 20px 0;
   padding: 0 15px;
`;

export const ImageContainer = styled.div`
    position: relative;
`;

export const SliderHeader = styled.h1`
    margin: 25px;
`;

export const Image = styled.img`
    width: 1100px;
    height: 600px;
    border-radius: 10px;

`;

export const ArticleText = styled.h1`
    position: absolute;
    top: 50%;
    left: 50%;
    transform: translate(-50%, -50%);
    color: #fff;
`;
