import React, { useState } from 'react'
import Carousel from 'react-elastic-carousel';
import * as s from './NewsSlider.styling'
import { Images } from './SliderData'

import { getArticles } from '../../Requests/posts'


export const NewsSlider = () => {
    const breakPoints = [
        { width: 200, itemsToShow: 1 },
        { width: 600, itemsToShow: 2 },
        { width: 1100, itemsToShow: 3 },

    ];

    const [posts, setPosts] = useState([]);

    React.useEffect( () => {

      getArticles()
        .then((response) => {
          console.log("response:", response);
          setPosts(response.data)
      })
        .catch((error) => console.log("error:", error))

    }, []);

    console.log(posts);

    return (
      <s.SliderWrap>
        <s.SliderHeader>Breaking News of the hour</s.SliderHeader>  
        <Carousel breakPoints={breakPoints}
                  transitionMs={700}
                  > 

                {posts.map((slide, index) => {
                    return (
                        <s.ImageContainer>
                            <img key={slide.image} src={slide.imageUrl} alt="space image" />
                            <s.TextContainer>
                              <h3 key={slide.title}>{slide.title}</h3>
                              <h6 key={slide.summary}>{slide.summary}</h6>  
                            </s.TextContainer>                          
                        </s.ImageContainer>
                    )
                })}

        </Carousel>     
     </s.SliderWrap>   
    )
}
