import styled from 'styled-components'
import { Button } from '../../globalStyles'


export const NewsSection = styled.div`
	padding: 100px 0;
	background: #75A5AC;
`;

export const NewsFlexGrid = styled.div`
    max-width: 1100px;
	display: flex;
	margin: 0 auto;
	flex-wrap: wrap;
	align-items: center;
    justify-content: space-evenly;
	flex-direction: row;

`;

export const NewsCartWrap = styled.div`
    width: 300px;
    height: 400px;
    margin: 10px 0;
    padding: 7px;
    border: 1px solid green;
    border-radius: 4px;
    background: #fff;
    display: flex;
    flex-direction: column;
`;

export const NewsCartImage = styled.img`
    width: 100%;
    height: 70%;
    object-fit: cover;
`;

export const NewsCartButton = styled(Button)`


    ${Button}
`;
