import styled from "styled-components";
import {Container} from '../../globalStyles'
import {DiMeteor} from 'react-icons/di'
import {Link} from 'react-router-dom'


export const Nav = styled.nav`
     background: #2a6973;
     height: 80px;
     display: flex;
     justify-content: center;
     align-items: center;
     font-size: 1.2rem;
     position: sticky;
     top: 0;
     z-index: 999;
`;

export const NavbarContainer = styled(Container)`
     display: flex;
     justify-content: space-between;
     height: 80px;

     ${Container}
`;

export const NavLogo = styled(Link)`
     color: #fff;
     justify-self: flex-start;
     cursor: pointer;
     text-decoration: none;
     font-size: 2rem;
     display: flex;
     align-items: center;
     /* text-shadow: 5px 15px 10px #888888; */
     text-shadow: 0 0 5px #888888;

`;

export const NavIcon = styled(DiMeteor)`
     width: 80px;
     height: 80px;
     margin-right: 0.5rem;
`;

export const HamburgerMenu = styled.div`
     display: none;

     @media screen and (max-width: 960px) {
          display: block;
          position: absolute;
          top: 0;
          right: 0;
          transform: translate(-100%, 60%);
          font-size: 1.8rem;
          cursor: pointer;
     }
`;

export const NavMenu = styled.ul`
     display: flex;
     align-items: center;
     list-style: none;
     text-align: center;

     @media screen and (max-width: 960px) {
          display: flex;
          flex-direction: column;
          width: 100%;
          height: 90vh;
          position: absolute;
          top: 80px;
          left: ${({click}) => (click ? 0 : '-100%')};
          opacity: n1;
          transition: all 0.5s ease;
          background: #053C44;
     }
`;

export  const NavItem = styled.li`
     height: 80px;
     border-bottom: 2px solid transparent;

     &:hover {
          border-bottom: px solid black;
     }

     @media screen and (max-width: 960px) {
          width: 100%;

          &:hover {
               border: none;
          }
     }
`;

export const NavLinks = styled(Link)`
     color: #fff;
     display: flex;
     align-items: center;
     text-decoration: none;
     padding: 0.5rem 1rem;
     height: 100%;


     @media screen and (max-width: 960px) {
          text-align: center;
          padding: 2rem;
          width: 100%;
          display: table;

          &:hover {          
               border: 2px solid black;
               transition: all 0.3s ease;
          }
     }
`;


export const NavItemBtn = styled.li`
     @media screen and (max-width: 960px) {
          display: flex;
          justify-content: center;
          align-items: center;
          width: 100%;
          height: 120px;
     }
`;

export const NavBtnLink = styled(Link)`
     display: flex;
     justify-content: center;
     align-items: center;
     text-decoration: none;
     padding: 8px 16px;
     height: 100%;
     width: 100%;
     border: none;
     outline: none;
`













