import styled from "styled-components";

export const HeaderStyle = styled.div`
     background-color: black;
     height: 200px;
     width: 300px;
     margin: 20px auto;
     border-radius: 5px;
     text-align: center;
     color: white;

     &:hover {
         background-color: green;
     }

`

// export default HeaderStyle;
