import React, { useState } from 'react'
import Carousel from 'react-elastic-carousel';
import * as s from './NewsSlider.styling'
import { Images } from './SliderData'
import { getArticles } from '../../Requests/posts'


export const NewsSlider = () => {
    const breakPoints = [
        { width: 200, itemsToShow: 1 },
        { width: 600, itemsToShow: 2 },
        { width: 1100, itemsToShow: 3 },

    ];

    const [posts, setPosts] = useState([]);

    React.useEffect( () => {
      // This makes a get request according to the link when the component is mounted
      getArticles()
        .then((response) => {
          console.log("response:", response);
          setPosts(response.data)
      })
        .catch((error) => console.log("error:", error))

    }, []);

function randomArticle () {
    const random = Math.floor(Math.random() * posts.length);
    console.log(posts.length);
    // return posts[random];
}

    return (
      <s.SliderWrap>  
        <Carousel breakPoints={breakPoints}> 

                {Images.map((slide, index) => {
                    return (
                        <s.ImageContainer>
                            <s.Image key={slide.image} src={slide.image} alt="space image" />
                            <s.ArticleText key={randomArticle.title}>                       
                                     {randomArticle.title}                            
                            </s.ArticleText>
                        </s.ImageContainer>
                    )
                })}

        </Carousel>     
     </s.SliderWrap>   
    )
}
