import styled from "styled-components";

export const Container = styled.div`
     width: 90%;
     margin: 0 auto;
`

export const Wrapper = styled.div`
	max-width: 1100px;
    display: flex;
	justify-content: space-between;

`
export const HeaderText = styled.div`
	margin-top: 135px;
	width: 540px;
    color: white;

`
export const Title = styled.h1`
	font-size: 50px;
	margin-bottom: 25px;
`
export const Spaceship = styled.span`
	color: #99EFD0;
	font-size: 60px;
`
export const Description = styled.p`
	font-style: italic;
	margin-bottom: 25px;

`

export const DesktopImage = styled.img`
	width: 600px;
	height: 500px;

`
export const Button = styled.button` 
	background: #ed9619;
	color: #fff;
	border-radius: 4px;
	display: block;
	width: 157px;
 	padding: 14px 0;
 	text-align: center;
 	text-transform: uppercase;
 	transition: all 0.3s ease;
	border: none;


  &:hover {
	background: #d9860e;
}
`
