import styled from 'styled-components'

export const NewsSection = styled.div`
	color: #fff;
	padding: 100px 0;
	background: #75A5AC;
`;

export const NewsFlexGrid = styled.div`
	display: flex;
	margin: 0 -15px -15px -15px;
	flex-wrap: wrap;
	align-items: center;
    justify-content: space-evenly;
	flex-direction: row;
`;
