import React, { useState, useEffect } from 'react';
import * as s from './SignUp.styling';


export const SignUp = () => {
  const [values, setValues] = useState({username: "", email: "", password: "", password2: ""});
  const [errors, setErrors] = useState({username: "", email: "", password: "", password2: ""});
  const [isSubmitting, setIsSubmitting] = useState(false);

  const handleChangeRegister = event => {
    console.log(event.target.name);
    console.log(event.target.value);

    setValues({
      ...values,
      [event.target.name]: event.target.value
    });
  };

  function validate(values) {
    let errors = {};
    if (!values.login) {
      errors.username = "Username is required";
    } else if (values.login.length < 8) {
      errors.username = "Username should be 8 or more characters";
    }
    if (!values.password) {
      errors.password = "Password is required";
    } else if (values.password.length < 10) {
      errors.password = "Password needs to be more than 10 characters";
    }
    return errors;
  }

  const handleSubmit = event => {
    event.preventDefault();
    setErrors(validate(values));
    setIsSubmitting(true);
  }


  useEffect(() => {
    if (Object.keys(errors).length === 0 && isSubmitting) {
      submit();

    }
  }, [errors])

  function submit() {
    localStorage.setItem("credentials", JSON.stringify(values));
    document.getElementById("register-field").style.display = "none";
    document.getElementById("registered-info").innerHTML = "You are registered now! Please log in.";
  }


    return (
        <>
            <s.SignUpSection>
                <s.FormContainer>
                    <s.SignUpFormWrap>                   
                        <s.FormOption>
                            <h1>Sign up</h1>                            
                            <s.FormInput type="text"
                                        name="username"
                                        placeholder="Username"
                                        value={values.username}
                                        onChange={handleChangeRegister}
                                        />
                            <s.FormInput type="text"
                                        name="email"
                                        placeholder="E-mail"
                                        value={values.email}
                                        onChange={handleChangeRegister}
                                        />
                            <s.FormInput type="password"
                                        name="password"
                                        placeholder="Password"
                                        value={values.password}
                                        onChange={handleChangeRegister}
                                        />
                            <s.FormInput type="password" name="password2" placeholder="Retype password" />

                            <s.SubmitButton type="submit" fontBig orange>Sign me up</s.SubmitButton>
                        </s.FormOption>
                        <s.Or>OR</s.Or>
                        <s.SocialMediaOption>
                            <h3>Sign in with social network</h3>

                            <s.SubmitButton fontBig>Log in with Facebook</s.SubmitButton>
                            <s.SubmitButton fontBig>Log in with Twitter</s.SubmitButton>
                            <s.SubmitButton fontBig>Log in with Google+</s.SubmitButton>
                        </s.SocialMediaOption>                        
                    </s.SignUpFormWrap>
                </s.FormContainer>
            </s.SignUpSection>
        </>
    )
}
