import styled from 'styled-components'

export const SliderWrap = styled.section`
    /* position: relative; */
    height: 100vh;
    display: flex;
    justify-content: center;
    align-items: center;
`;

export const Image = styled.img`
    width: 1100px;
    height: 600px;
    border-radius: 10px;

`;

export const RightArrow = styled.div`

    top: 50%;
    right: 32px;
    font-size: 3rem;
    color: #000;
    z-index: 10;
    cursor: pointer;
    user-select: none;
`;

export const LeftArrow = styled.div`
    position: absolute;
    top: 50%;
    left: 32px;
    font-size: 3rem;
    color: #000;
    z-index: 10;
    cursor: pointer;
    user-select: none;
`
