import styled from "styled-components";

export const HeaderStyle = styled.div`
     background-color: black;
     height: 200px;
     width: 300px;
     margin: 20px auto;
     border-radius: 5px;
     text-align: center;
     color: white;

     &:hover {
         background-color: green;
     }

     backgroundColor: '#336f33',
        height: '100px',
        width: '200px',
        margin: '10px auto',
        color: 'yellow',
        textAlign: 'center',
        verticalAlign: 'center',
        padding: '10px',
        fontSize: '20px',
        borderRadius: '5px'
`

// export default HeaderStyle;
