import React, { useState, useEffect } from 'react';
import * as s from './SignUp.styling';


export const SignUp = () => {
  const [values, setValues] = useState({username: "", password: ""});
  const [errors, setErrors] = useState({username: "", password: ""});
  const [isSubmitting, setIsSubmitting] = useState(false);

  const handleChangeRegister = event => {
    console.log(event.target.name);
    console.log(event.target.value);

    setValues({
      ...values,
      [event.target.name]: event.target.value
    });
  };


  const handleSubmit = event => {
    event.preventDefault();
    setErrors(validate(values));
    setIsSubmitting(true);
  }


  useEffect(() => {
    if (Object.keys(errors).length === 0 && isSubmitting) {
      submit();

    }
  }, [errors])

  function submit() {
    localStorage.setItem("credentials", JSON.stringify(values));
    document.getElementById("register-field").style.display = "none";
    document.getElementById("registered-info").innerHTML = "You are registered now! Please log in.";
  }


    return (
        <>
            <s.SignUpSection>
                <s.FormContainer>
                    <s.SignUpFormWrap>                   
                        <s.FormOption>
                            <h1>Sign up</h1>                            
                            <s.FormInput type="text" name="username" placeholder="Username" />
                            <s.FormInput type="text" name="email" placeholder="E-mail" />
                            <s.FormInput type="password" name="password" placeholder="Password" />
                            <s.FormInput type="password" name="password2" placeholder="Retype password" />

                            <s.SubmitButton type="submit" fontBig orange>Sign me up</s.SubmitButton>
                        </s.FormOption>
                        <s.Or>OR</s.Or>
                        <s.SocialMediaOption>
                            <h3>Sign in with social network</h3>

                            <s.SubmitButton fontBig>Log in with Facebook</s.SubmitButton>
                            <s.SubmitButton fontBig>Log in with Twitter</s.SubmitButton>
                            <s.SubmitButton fontBig>Log in with Google+</s.SubmitButton>
                        </s.SocialMediaOption>                        
                    </s.SignUpFormWrap>
                </s.FormContainer>
            </s.SignUpSection>
        </>
    )
}
