import styled from "styled-components";


export const InfoSection = styled.div`
	color: #fff;
	padding: 160px 0;
	background: ${({lightBg}) => (lightBg ? '#fff' : '#75A5AC')};
`;

export const InfoRow = styled.div`
	display: flex;
	margin: 0 -15px -15px -15px;
	flex-wrap: wrap;
	align-items: center;
	flex-direction: ${({imgStart}) => (imgStart ? 'row-reverse' : 'row')};

`;

export const InfoColumn = styled.div`
	margin-bottom: 15px;
	padding-right: 15px;
	padding-left: 15px;
	flex: 1;
	max-width: 50%;
	flex-basis: 50%;

	@media screen and (max-width: 768px) {
		max-width: 100%;
		flex-basis: 100%;
		display: flex;
		justify-content: center;
	}
`;

export const TextWrapper = styled.div`
	max-width: 540px;
	padding-top: 0;
	padding-bottom: 60px;

	@media screen and ( max-width: 768px) {
		padding-bottom: 65px;
	}
`;

export const TopLine = styled.div`
	color: ${({lightTopLine}) => (lightTopLine ? '#F2994A' : '#2a6973')};
	font-size: 18px;
	line-height: 16px;
	letter-spacing: 1.4px;
	margin-bottom: 16px;
`;

export const Heading = styled.h1`
	margin-bottom: 24px;
	font-size: 48px;
	line-height: 1.1;
	color: ${({lightText}) => (lightText ? '#9CB2B5' : 'white')};
`
